parameters.param:
[CmdletBinding()] Param ( [string] $workingDir = "%teamcity.build.workingDir%", [string] $nugetExe = "%xUnitNet.nugetExe%\tools\nuget.exe", [string] $xUnitNuget = "%xUnitNet.nugetSource%", [string] $xUnitExe = "%xUnitNet.executable%", [string] $dotCoverExecutable = "%teamcity.tool.dotCover%", [boolean] $xunitLegacy = [System.Convert]::ToBoolean("%xUnitNet.executable.legacymode%"), [string] $assemblyFilter = "%xUnitNet.assembliesPath%", [string] $assemblyExcludeFilter = "%xUnitNet.assembliesExcludePath%", [string] $traits = "%xUnitNet.trait%", [string] $notraits = "%xUnitNet.notrait%", [boolean] $dotCoverEnabled = [System.Convert]::ToBoolean("%xUnitNet.dotCover.enable%"), [boolean] $dotCoverReportEnabled = [System.Convert]::ToBoolean("%xUnitNet.dotCover.exportReport%"), [string] $dotCoverReportType = "%xUnitNet.dotCover.reportType%", [string] $dotCoverReportFile = "%xUnitNet.dotCover.reportOutputFile%", [string] $dotCoverFilter = "%xUnitNet.dotCover.Filters%", [string] $dotCoverAttributeFilter = "%xUnitNet.dotCover.AttributeFilters%", [string] $xunitArgs = "%xUnitNet.executable.args%" )
$ErrorActionPreference = "Stop"
try {
## get xunit runner from nuget package feed iex "$nugetExe install xunit.runner.console -source $xUnitNuget" $xunit = Join-Path $workingDir "xunit.runner.console.*\tools\$xUnitExe" | Resolve-Path
## modify runner config if legacy v2 runtime activation mode required If($xunitLegacy) { Write-Host "Setting xunit to run with legacy activation policy" $config = [string]$xunit + ".config" $doc = (Get-Content $config) -as [Xml] $doc.configuration.startup.SetAttribute("useLegacyV2RuntimeActivationPolicy", "true") $doc.Save($config) }
## Process Assemblies to be Excluded ## split string into array if multiple wildcard path separated by comma(,) or newline $assemblyExcludeFilterPaths = $assemblyExcludeFilter.Trim() -split {$_ -eq "," -or $_ -eq "`n" } $excludedAssemblies = @()
foreach($filterExcludePath in $assemblyExcludeFilterPaths) { ## Concatenate each Assembly into the array $excludedAssemblies +=,$filterExcludePath.Trim() }
##split string into array if multiple wildcard path separated by comma(,) or newline $assemblyFilterPaths = $assemblyFilter.Trim() -split {$_ -eq "," -or $_ -eq "`n" }
$assemblies = @()
foreach($filterPath in $assemblyFilterPaths) { ## Search for the assemblies using wildcard pattern (Also filter out excluded assemblies) $assembliesFromPath = (Get-ChildItem $filterPath.Trim() -Recurse -Exclude $excludedAssemblies.Trim() | select -ExpandProperty FullName) $assemblies+= $assembliesFromPath }
if($assemblies -eq $null) { $cwd = Get-Location Write-Host "No test assemblies found; cwd = $cwd ; assembly filter = $assemblyFilter" return } else { Write-Host "Found test assemblies: " $assemblies }
## create single-line correctly formatted xunit argument
## the type will be a string for a single-line path without a wildcard. if ($assemblies.GetType() -eq [String]) { $assemblies = [String[]] $assemblies }
# split multi-line xunit args into single line and correctly format $assemblyArg = "`"" + [String]::Join("`" `"", $assemblies) + "`"" If($traits.Length -gt 0) { $traitArg = ($traits.Trim().Split("`r`n") | where {$_ -ne ""} | % { "-trait """+$_+""""}) -join " " } If($notraits.Length -gt 0) { $notraitArg = ($notraits.Trim().Split("`r`n") | where {$_ -ne ""} | % { "-notrait """+$_+""""}) -join " " } $xunitArg = $assemblyArg, $traitArg, $notraitArg, $xunitArgs -join " "
## filenames $outputFile = "xunitcoverage.dcvr" $settingsFileName = "coverage_settings.xml" $coverageLogFileName = "dotCoverXunitLog.txt"
Write-Host "##teamcity[message text='Run test coverage: $dotCoverEnabled']"
## do we run coverage? If($dotCoverEnabled) { ## generate coverage settings file $settingsXml = @" <?xml version="1.0" encoding="utf-8"?> <CoverageParams> <TargetExecutable>$xunit</TargetExecutable> <TargetArguments>$xunitArg</TargetArguments> <TargetWorkingDir>$workingDir</TargetWorkingDir> <Output>$outputFile</Output> $dotCoverFilter $dotCoverAttributeFilter </CoverageParams> "@ # Avoid BOM http://stackoverflow.com/questions/5596982/using-powershell-to-write-a-file-in-utf-8-without-the-bom $settingsXml | Out-File $settingsFileName -Encoding ASCII
## Run coverage $dotCover = Join-Path $dotCoverExecutable "dotCover.exe" $dotCoverArgs = " cover " + $settingsFileName + " /LogFile=$coverageLogFileName /ReturnTargetExitCode" Write-Host "##teamcity[message text='dotCoverExecutable: $dotCoverExecutable']" Write-Host "##teamcity[message text='dotCoverArgs: $dotCoverArgs']" $command = $dotCover + $dotCoverArgs iex $command
## Report coverage results and output build artifacts write-host "##teamcity[importData type='dotNetCoverage' tool='dotcover' path='$outputFile']" write-host "##teamcity[publishArtifacts '$settingsFileName']" write-host "##teamcity[publishArtifacts '$coverageLogFileName']" write-host "##teamcity[publishArtifacts '$outputFile']"
## Optional generate report If($dotCoverReportEnabled) { iex "$dotCover report /source=$outputFile /output=$dotCoverReportFile /reportType=$dotCoverReportType" write-host "##teamcity[publishArtifacts '$dotCoverReportFile']" }
} Else { ## run xunit without coverage iex "$xunit $xunitArg" } } catch {
Write-Host "##teamcity[buildStatus text='$_' status='FAILURE']" Write-Host "##teamcity[message text='$_' status='ERROR']" exit 1 }
